import React, { useRef, useState } from "react";

const tracks = [
  {
    title: "The Volvo Ocean Race",
    artist: "Nannette Midwood & Keith Byrne",
    src: "/audio/volvo.mp3",
    duration: "07:26",
  },
  {
    title: "Formula One Paddock",
    artist: "Nannette Midwood & Keith Byrne",
    src: "/audio/formula.mp3",
    duration: "07:59",
  },
  {
    title: "Desert Challenge Champion - Sean Linton",
    artist: "Sean Linton",
    src: "/audio/desert.mp3",
    duration: "10:01",
  },
    {
    title: "Camel Riding",
    artist: "Nannette Midwood & Chris Heyne",
    src: "/audio/camel.mp3",
    duration: "10:01",
  },
    {
    title: "Desert Challenge Champion - Sean Linton",
    artist: "Sean Linton",
    src: "/audio/desert.mp3",
    duration: "10:01",
  },
];

export default function AudioPlayer() {
  const audioRef = useRef(null);
  const [currentTrack, setCurrentTrack] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  const togglePlay = () => {
    const audio = audioRef.current;
    if (audio.paused) {
      audio.play();
      setIsPlaying(true);
    } else {
      audio.pause();
      setIsPlaying(false);
    }
  };

  const playTrack = (index) => {
    setCurrentTrack(index);
    setIsPlaying(true);
    setTimeout(() => audioRef.current.play(), 0); // Delay needed for track change
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-white shadow-lg rounded-xl">
      <div className="mb-4">
        <img
          src="/cover.jpg"
          alt="Podcast Cover"
          className="w-32 h-32 object-cover mx-auto rounded"
        />
        <h2 className="text-center font-bold mt-2 text-xl">{tracks[currentTrack].title}</h2>
        <p className="text-center text-sm text-gray-600">{tracks[currentTrack].artist}</p>
      </div>

      <div className="flex justify-center items-center space-x-4">
        <button onClick={() => playTrack((currentTrack - 1 + tracks.length) % tracks.length)}>
          ⏮️
        </button>
        <button onClick={togglePlay} className="text-2xl">
          {isPlaying ? "⏸️" : "▶️"}
        </button>
        <button onClick={() => playTrack((currentTrack + 1) % tracks.length)}>
          ⏭️
        </button>
      </div>

      <audio
        ref={audioRef}
        src={tracks[currentTrack].src}
        onEnded={() => playTrack((currentTrack + 1) % tracks.length)}
      />

      <div className="mt-4">
        <ol>
          {tracks.map((track, index) => (
            <li
              key={index}
              className={`p-2 hover:bg-gray-100 cursor-pointer rounded ${
                currentTrack === index ? "bg-gray-200 font-semibold" : ""
              }`}
              onClick={() => playTrack(index)}
            >
              {index + 1}. {track.title} — {track.duration}
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
}
