// 
import React from 'react';
import { Container, Grid, Typography, Box } from '@mui/material';
import './App.css';

const credits = [
  'DEN OF THIEVES 2.png', 'FROM.png', 'PRISCILLA.png', 'THE CONSULTANT.png',
  'THE NUTCRACKERS.png', 'CHAPELWAITE.jpg', 'BILLY.jpg', 'GEORGETOWN.png', 'DUAL.jpg'
];

function Section({ title, children }) {
  return (
    <Container sx={{ pl: 10, pr: 10, mb: 4 }}>
      <Typography variant="h4" sx={{ pt: 3, pb: 3, fontWeight: 'bold' }}>{title}</Typography>
      <Box className="box">{children}</Box>
    </Container>
  );
}

function App() {
  return (
    <>
      {/* Header */}
      <Container>
        <Grid container justifyContent="flex-end" alignItems="center" sx={{ pt: 5, pr: 10 }}>
          <img src="/pics/Ryan Allam Logo.png" className="logo-img" alt="Logo" />
          <img src="/pics/Ryan Allam Logo_name.png" className="logo-name" alt="Logo Name" />
        </Grid>
      </Container>

      {/* Selected Credits */}
      <Section title="Selected Credits">
        <Container id="grid" sx={{ pl: 0, pr: 0 }}>
          <Grid container spacing={2}>
            {credits.map((src, i) => (
              <Grid item xs={12} sm={6} md={4} key={i}>
                <Box className="credit-box">
                  <img src={`/pics/${src}`} alt={`Credit ${i + 1}`} className="credit-img" />
                  <span className="zoom-overlay">press to zoom</span>
                </Box>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Section>

      {/* About */}
      <Section title="About">
        <Box sx={{ display: 'flex', alignItems: 'flex-start', flexWrap: 'wrap' }}>
          <img src="/pics/Ryan_bio.jpg" className="bio-image" alt="Bio" />
          <Typography component="p" className="stretch-text" sx={{ ml: 2 }}>
            Ryan Joe Allam is a trilingual sound editor and sound designer based in Toronto...
            {/* Add full bio content here */}
          </Typography>
        </Box>
      </Section>

      {/* Podcasts */}
      <Section title="Podcasts">
        <Typography component="p" className="stretch-text">
          In February 2009, I was asked to produce a podcast with Nanette Midwood...
        </Typography>
      </Section>

      {/* Mixing */}
      <Section title="Mixing">
        <Typography component="p" className="stretch-text">
          In my constant quest to better myself, I joined Berklee College of Music...
        </Typography>
      </Section>

      {/* Contact */}
      <Section title="Contact">
        <Typography component="p" className="stretch-text">
          Do you have any question? Or just thinking about a project? Drop me a line at ryanallam@gmail.com...
        </Typography>
      </Section>

      {/* Footer */}
      <Container sx={{ py: 4 }}>
        <Typography variant="body2" align="center" className="stretch-text">
          2008 - 2025 © by Ryan Joe Allam<br />
          The content, logos and brand name appearing on this Site are the property of their respective owners.
        </Typography>
      </Container>
    </>
  );
}

export default App;
