import React, { useRef, useState } from "react";
import {
  Box,
  Typography,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  LinearProgress,
  Stack,
  Divider,
  Paper,
} from "@mui/material";
import {
  PlayArrow,
  Pause,
  SkipNext,
  SkipPrevious,
  PlayCircleOutline,
} from "@mui/icons-material";

export default function AudioPlayer({ tracks, logo }) {
  const audioRef = useRef(null);
  const [currentTrack, setCurrentTrack] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);

  const togglePlay = () => {
    const audio = audioRef.current;
    if (audio.paused) {
      audio.play();
      setIsPlaying(true);
    } else {
      audio.pause();
      setIsPlaying(false);
    }
  };

  const handleTimeUpdate = () => {
    const audio = audioRef.current;
    const percent = (audio.currentTime / audio.duration) * 100;
    setProgress(percent);
  };

  const playTrack = (index) => {
    if (index === currentTrack) {
      // Toggle if clicking on the same track
      togglePlay();
    } else {
      setCurrentTrack(index);
      setIsPlaying(true);
      setTimeout(() => audioRef.current.play(), 100);
    }
  };

  return (
    <Paper
      sx={{
        maxWidth: '100%',
        margin: "auto",
        p: 3,
        backgroundColor: "#000",
        color: "#fff",
        borderRadius: 2,
      }}
      elevation={8}
    >
      <Stack direction="row" spacing={2} alignItems="center">
        <Box
          component="img"
          src={logo}
          alt="image"
          sx={{ width: 100, height: 100, borderRadius: "50%" }}
        />
        <Box flexGrow={1}>
          {/* ðŸŽ¯ Moved player controls ABOVE the track info */}
          <Stack direction="row" spacing={2} alignItems="center" mb={1}>
            <IconButton onClick={() => playTrack((currentTrack - 1 + tracks.length) % tracks.length)} color="inherit">
              <SkipPrevious />
            </IconButton>
            <IconButton onClick={togglePlay} color="inherit">
              {isPlaying ? <Pause /> : <PlayArrow />}
            </IconButton>
            <IconButton onClick={() => playTrack((currentTrack + 1) % tracks.length)} color="inherit">
              <SkipNext />
            </IconButton>
            <Typography variant="caption">
              {tracks[currentTrack].duration}
            </Typography>
          </Stack>

          {/* ðŸŽ¯ Track info BELOW controls now */}
          <Typography fontWeight="bold" fontSize={18}>
            {tracks[currentTrack].title}
          </Typography>
          <Typography fontSize={14}>
            {tracks[currentTrack].artist}
          </Typography>
          <Typography fontSize={14} color="gray">
            {tracks[currentTrack].otherInfo}
          </Typography>
        </Box>
      </Stack>

      <Box mt={2}>
        <audio
          ref={audioRef}
          src={tracks[currentTrack].src}
          onTimeUpdate={handleTimeUpdate}
          onEnded={() => playTrack((currentTrack + 1) % tracks.length)}
        />

        <LinearProgress
          variant="determinate"
          value={progress}
          sx={{ mt: 2, backgroundColor: "#222", height: 4 }}
        />
      </Box>

      <Divider sx={{ my: 3, borderColor: "#333" }} />

      <List dense sx={{ width: '100%', bgcolor: 'transparent' }}>
        {tracks.map((track, index) => {
          const isCurrent = currentTrack === index;
          const isTrackPlaying = isCurrent && isPlaying;

          return (
            <ListItem
              key={index}
              button
              onClick={() => playTrack(index)}
              sx={{
                color: isCurrent ? "#f44336" : "#fff",
                borderBottom: "1px solid #222",
                py: 1,
              }}
            >
              <ListItemIcon sx={{ color: isCurrent ? "#f44336" : "#fff" }}>
                {isTrackPlaying ? <Pause /> : <PlayArrow />}
              </ListItemIcon>
              <ListItemText
                primary={track.title}
                primaryTypographyProps={{
                  fontSize: 14,
                }}
              />
              <Typography variant="caption" color={isCurrent ? "error" : "gray"}>
                {track.duration}
              </Typography>
            </ListItem>
          );
        })}
      </List>
    </Paper>
  );
}
