import React, { useRef, useState, useEffect } from "react";
import {
  Box,
  Typography,
  IconButton,
  Stack,
  Slider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
} from "@mui/material";
import {
  PlayArrow,
  Pause,
  SkipNext,
  SkipPrevious,
  VolumeUp,
  PlaylistPlay,
  FiberManualRecord,
} from "@mui/icons-material";

const tracks = [
  {
    title: "The Volvo Ocean Race",
    artist: "Nannette Midwood & Keith Byrne",
    src: "/audio/podcasts/volvo.mp3",
    duration: "07:26",
  },
  {
    title: "Formula One Paddock",
    artist: "Nannette Midwood & Keith Byrne",
    src: "/audio/podcasts/formula1.mp3",
    duration: "07:59",
  },
  {
    title: "Desert Challenge Champion - Sean Linton",
    artist: "Sean Linton",
    src: "/audio/podcasts/desert.mp3",
    duration: "10:01",
  },
  {
    title: "Camel Riding",
    artist: "Nannette Midwood & Chris Heyne",
    src: "/audio/podcasts/camel.mp3",
    duration: "01:18",
  },
  {
    title: "Yas Waterworld",
    artist: "Nannette Midwood & Chris Heyne",
    src: "/audio/podcasts/yas.mp3",
    duration: "08:41",
  },
];

const formatTime = (seconds) => {
  const mins = Math.floor(seconds / 60) || 0;
  const secs = Math.floor(seconds % 60) || 0;
  return `${mins.toString().padStart(2, "0")}:${secs.toString().padStart(2, "0")}`;
};

export default function AudioPlayer() {
  const audioRef = useRef(null);
  const [currentTrack, setCurrentTrack] = useState(4); // Yas Waterworld
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [duration, setDuration] = useState(0);

  useEffect(() => {
    const audio = audioRef.current;
    const updateProgress = () => setProgress(audio.currentTime);
    audio.addEventListener("timeupdate", updateProgress);
    audio.addEventListener("loadedmetadata", () => setDuration(audio.duration));
    return () => {
      audio.removeEventListener("timeupdate", updateProgress);
    };
  }, [currentTrack]);

  const togglePlay = () => {
    const audio = audioRef.current;
    if (audio.paused) {
      audio.play();
      setIsPlaying(true);
    } else {
      audio.pause();
      setIsPlaying(false);
    }
  };

  const playTrack = (index) => {
    setCurrentTrack(index);
    setIsPlaying(true);
    setTimeout(() => audioRef.current.play(), 100);
  };

  const skipTrack = (dir) => {
    const next = (currentTrack + dir + tracks.length) % tracks.length;
    playTrack(next);
  };

  return (
    <Box
      sx={{
        maxWidth: 600,
        mx: "auto",
        p: 2,
        bgcolor: "#000",
        color: "#fff",
        borderRadius: 2,
      }}
    >
      {/* Top Info Row */}
      <Stack direction="row" spacing={2} alignItems="center" mb={2}>
        <Box
          component="img"
          src="/logos/etihad_logo.png"
          alt="Cover"
          sx={{ width: 80, height: 80, objectFit: "contain", borderRadius: "50%" }}
        />
        <Box>
          <Typography variant="body2">{tracks[currentTrack].artist}</Typography>
          <Typography variant="h6" sx={{ fontWeight: "bold" }}>
            {tracks[currentTrack].title}
          </Typography>
          <Typography variant="caption" color="gray">
            ETIHAD HORIZONS
          </Typography>
        </Box>
      </Stack>

      {/* Controls */}
      <Stack direction="row" alignItems="center" spacing={2} mb={1}>
        <IconButton onClick={() => skipTrack(-1)} color="error">
          <SkipPrevious />
        </IconButton>
        <IconButton onClick={togglePlay} color="error">
          {isPlaying ? <Pause /> : <PlayArrow />}
        </IconButton>
        <Typography variant="caption">
          {formatTime(progress)} / {formatTime(duration)}
        </Typography>
        <Box flexGrow={1} />
        <IconButton disabled>
          <VolumeUp sx={{ color: "gray" }} />
        </IconButton>
        <IconButton disabled>
          <PlaylistPlay sx={{ color: "gray" }} />
        </IconButton>
      </Stack>

      {/* Progress Bar */}
      <Slider
        value={progress}
        max={duration}
        onChange={(e, val) => {
          audioRef.current.currentTime = val;
          setProgress(val);
        }}
        sx={{
          color: "red",
          height: 4,
          mb: 3,
        }}
      />

      {/* Audio Element */}
      <audio
        ref={audioRef}
        src={tracks[currentTrack].src}
        onEnded={() => skipTrack(1)}
      />

      {/* Track List */}
      <List dense disablePadding>
        {tracks.map((track, index) => (
          <ListItem
            key={index}
            disableGutters
            onClick={() => playTrack(index)}
            sx={{
              px: 1,
              py: 0.5,
              borderBottom: "1px solid red",
              color: index === currentTrack ? "red" : "white",
              cursor: "pointer",
              "&:hover": { bgcolor: "#111" },
            }}
          >
            <ListItemIcon sx={{ minWidth: 24 }}>
              {index === currentTrack ? (
                <PlayArrow fontSize="small" sx={{ color: "red" }} />
              ) : (
                <Typography variant="caption">{index + 1}</Typography>
              )}
            </ListItemIcon>
            <ListItemText
              primary={track.title}
              primaryTypographyProps={{ fontSize: 14 }}
            />
            <Typography variant="caption">{track.duration}</Typography>
          </ListItem>
        ))}
      </List>
    </Box>
  );
}
